declare module 'socket.io-file-client' {

  interface SocketIOFileClient {
    new (socket: SocketIOClient.Socket): SocketIOFileClient
    on: (event: string, callback: (...args: any) => void) => void
    upload: (files: FileList, data?: { [key: string]: any }) => UploadIds
  }
  const ctor: SocketIOFileClient
  export default ctor

  export type UploadIds = string[]

  export type FileInfo = {
    uploadId: string
    name: string
  }
}
